configuration.config:
type Loaders is ("ROM", "RAM", "USER");

Loader : Loaders := external ("LOADER", "ROM");

package Compiler is
   Common_Required_Switches := ("-mlittle-endian",
                                "-mcpu=cortex-m0plus", "-mthumb",
                                "-fno-omit-frame-pointer");

   for Leading_Required_Switches ("Ada") use
      Compiler'Leading_Required_Switches ("Ada") &
      Common_Required_Switches;

   for Leading_Required_Switches ("C") use
      Compiler'Leading_Required_Switches ("C") &
      Common_Required_Switches;
end Compiler;

package Linker is
   for Required_Switches use Linker'Required_Switches &
     ("-L${RUNTIME_DIR(ada)}/adalib", "-nolibc", "-nostartfiles", "-Wl,-Map=mapfile.map") &
     Compiler.Common_Required_Switches;

   case Loader is
      when "USER" =>
      when others =>
         for Required_Switches use Linker'Required_Switches &
         ("-L", "${RUNTIME_DIR(ada)}/../../bsps/kinetis_kl25z_frdm/bsp/",
          "-T", "memory-map.ld",
          "-T", "common-" & Loader & ".ld");
  end case;
end Linker;
